    var setSubjectLevelSearchLabelDynamically = function(){
        var lowLevelId = $("#subjectLowLevelId").val();
        var highLevelId = $("#subjectHighLevelId").val();
        if(isNotBlank(lowLevelId) || isNotBlank(highLevelId)){
            //var levelFilterText = "Level (";
            var levelFilterText = "";
            if(isNotBlank(lowLevelId) && isNotBlank(highLevelId)){
                var lowLevelText = levelNameOrShortName[lowLevelId];
                var highLevelLevelText = levelNameOrShortName[highLevelId];
                if(lowLevelText == highLevelLevelText){
                    levelFilterText = levelFilterText + lowLevelText;
                } else {
                    levelFilterText = levelFilterText + lowLevelText + ' - '+ highLevelLevelText;
                }
            } else if(isNotBlank(lowLevelId)){
                var lowLevelText = levelNameOrShortName[lowLevelId];
                // Find it's highest level and check whether it's same or not.
                var groupName = levelGroupByLevelJsonObj[lowLevelId];
                var highLevelIdTmp = minOrMaxLevelByGroupName[groupName].maxLevelId;
                if(isNotBlank(highLevelIdTmp) && lowLevelId == highLevelIdTmp){
                    // Keep single if low and high level are same.
                    levelFilterText = levelFilterText + lowLevelText;
                } else {
                    levelFilterText = levelFilterText + lowLevelText + ' and above';
                }
            } else if(isNotBlank(highLevelId)){
                var highLevelLevelText = levelNameOrShortName[highLevelId];
                var groupName = levelGroupByLevelJsonObj[highLevelId];
                var lowLevelIdTmp = minOrMaxLevelByGroupName[groupName].minLevelId;
                if(isNotBlank(lowLevelIdTmp) && highLevelId==lowLevelIdTmp){
                    levelFilterText = levelFilterText + highLevelLevelText;
                } else {
                    levelFilterText = levelFilterText + 'Up to '+ highLevelLevelText;
                }
            }
            levelFilterText = levelFilterText;
            $("#clsSubjectFilterModalLinkText").html(levelFilterText);
            styleLevelFilterButton();
        }
    };

