            styleTutorOrJobSearchHeader();
        } catch(e){}

        $(".clsSubjectFilterModalLink").on("click", function(){
            tmpLowLevelIdBeforeChange = $("#subjectLowLevelId").val();
            tmpHighLevelIdBeforeChange = $("#subjectHighLevelId").val();
            openModalCommon('levelFilterModal');
            $(".clsCloseLevelFilterModal").removeClass('hidden');
        });

        $(".clsResetAndCloseSubjectLevelFilterModal").on("click", function(){
            $("#subjectLowLevelId").val("");
            $("#subjectHighLevelId").val("");
              $("#clsSubjectFilterModalLinkText").html('Level');
            closeModalCommon('levelFilterModal');
            resetHighestLevel();

            // call ajax to search records based on filter changes
            if(isNotBlank(tmpLowLevelIdBeforeChange) || isNotBlank(tmpHighLevelIdBeforeChange)){
                tmpLowLevelIdBeforeChange = ''; tmpHighLevelIdBeforeChange='';
                lockForSearch();
            }
        });

        $(".clsApplySubjectLevelFilter").on("click", function(){
            var lowLevelId = $("#subjectLowLevelId").val();
            var highLevelId = $("#subjectHighLevelId").val();
            var isValid = isNotBlank(lowLevelId) || isNotBlank(highLevelId);
            if(isValid){
                setSubjectLevelSearchLabelDynamically();
            }
            closeModalCommon('levelFilterModal');

            var isLowLevelSame = false, isHighLevelSame = false;
            var isLowLevelSame = (isBlank(tmpLowLevelIdBeforeChange) && isBlank(lowLevelId)) ||
                    (isNotBlank(tmpLowLevelIdBeforeChange) && isNotBlank(lowLevelId) && tmpLowLevelIdBeforeChange == lowLevelId);
            var isHighLevelSame = (isBlank(tmpHighLevelIdBeforeChange) && isBlank(highLevelId)) ||
                    (isNotBlank(tmpHighLevelIdBeforeChange) && isNotBlank(highLevelId) && tmpHighLevelIdBeforeChange == highLevelId);

            // call ajax to search records based on filter changes
            if(!isLowLevelSame || !isHighLevelSame){
                tmpLowLevelIdBeforeChange = ''; tmpHighLevelIdBeforeChange='';
                lockForSearch();
            }
        });

        // If user select level filter but did not click on apply button still call this to search by that filter.
        $(".clsCloseLevelFilterModal").on("click", function(){
            closeModalCommon('levelFilterModal');
        });

        // adding this so that it can work on dynamically added class links.
        $(document).on('click', "a.searchLean", function(e) {
            //alert('called search lean...');
            e.preventDefault();
            var pageUrl = $(this).attr("href");
            var searchUrl = pageUrl.replaceAll('https://www.teacheron.com/','https://www.teacheron.com/search-lean/');
            var urlWithoutPrefix = searchUrl.replaceAll('https://www.teacheron.com/search-lean/', '');

            // logic to mark all links inactive and select currently clicked url
            // go through links and remove active and underline class
            // Don't reset in case of pagination flow
            $("#onlineHomeLinksUL > li").each(function () {
                $(this).removeClass('active').removeClass("underline");
            });
            // mark current selected <li> as active and make it underline. Also link are set using search method in below code.
            $(this).closest("li").addClass('active').addClass("underline");
            searchTutorsOrJobsAjax(searchUrl, pageUrl, urlWithoutPrefix, true);
        });


        // handle browser back click button. Read url and call ajax to load that results.
        $(window).bind("popstate", function (event) {
            var pageUrl = location.href;
            var searchUrl = pageUrl.replaceAll('https://www.teacheron.com/','https://www.teacheron.com/search-lean/');
            var urlWithoutPrefix = searchUrl.replaceAll('https://www.teacheron.com/search-lean/', '');
            try{
                var ajaxData = event.originalEvent.state.data;

                // reset currently active link and it will be set by next flow
                $("#onlineHomeLinksUL > li").each(function () {
                    $(this).removeClass('active').removeClass("underline");
                });

                // it will give error when data not found.
                processSearchLeanAjaxResponse(ajaxData, urlWithoutPrefix, pageUrl, false);
            } catch(e){
                try{
                     var pageUrl = location.href;
                     var searchUrl = pageUrl.replaceAll('https://www.teacheron.com/','https://www.teacheron.com/search-lean/');
                     var urlWithoutPrefix = searchUrl.replaceAll('https://www.teacheron.com/search-lean/', '');
                     $("#onlineHomeLinksUL > li").each(function () {
                     $(this).removeClass('active').removeClass("underline");
                     });
                     searchTutorsOrJobsAjax(searchUrl, pageUrl, urlWithoutPrefix, false);
                 } catch(e){
                    window.location.href = pageUrl;
                 }
            }

        });
    });

    var selectHighLevel = function(id, highestSubjectId,changeSubjectLevelLinkLabel){
        if(isNotBlank(id)){
            //$(".clsErrorLowLevel").hide();
            //$(".clsErrorHighLevel").hide();

            // case: If user select high level first then select low level in that case it should not reset high level
            var selectedHighLevelId = $("#subjectHighLevelId").val();
            $.ajax({
                url: 'https://www.teacheron.com/level-search',
                type: 'GET',
                data: 'lowLevelId='+id,
                success: function(data) {
                    if(data!=null && data!=''){
                        var optionData = '<select class="form-control" name="subjectHighLevelId" id="subjectHighLevelId">';
                        optionData = '<option value="">'+'-- Select level --'+'</option>';
                        var arr = data.split("@@");
                        for(var i=0; i<arr.length; i++){
                            var arrData = arr[i].split("##");
                            if(highestSubjectId!=null && highestSubjectId!='' && highestSubjectId!=undefined && highestSubjectId>0
                                    && arrData[0]==highestSubjectId){
                                optionData = optionData+"<option selected='selected' value='"+arrData[0]+"'>"+arrData[1]+"</option>";
                            } else {
                                optionData = optionData+"<option value='"+arrData[0]+"'>"+arrData[1]+"</option>";
                            }
                        }
                        optionData = optionData +"</select>";
                    } else {
                        optionData = optionData +"<option disabled value=''>No Level Found.</option>";
                        optionData = optionData +"</select>";
                    }
                    $('#subjectHighLevelId').html(optionData);

                    try{
                        // case: If user select high level first then select low level in that case it should not reset high level
                        if(isNotBlank(selectedHighLevelId) && selectedHighLevelId>0){
                            $("#subjectHighLevelId").val(selectedHighLevelId);
                            // check whether it's selected or not. If not then reset it otherwise it won't show placeholder
                            if(isBlank($("#subjectHighLevelId").val())){
                                $("#subjectHighLevelId").val(""); // reset it to blank so that it can show placeholder.
                            }
                        }
                    } catch(e){
                        console.log(e);
                    }
                    if(changeSubjectLevelLinkLabel){
                        setSubjectLevelSearchLabelDynamically();
                    }
                },
                error: function(e) {
                    //called when there is an error
                    console.log(e.message);
                }
            });
        } else {
            //$(".clsErrorLowLevel").show();
            resetHighestLevel();
        }
    };
</script>

<div class="overlay"></div>

<script src="https://maps.googleapis.com/maps/api/js?key=AIzaSyBauoyfg7a2YjQnqwJYe0pOg5nKzQKUHIQ&libraries=places&language=en&callback=initAutocomplete" async defer></script>
<script type="text/javascript">
    var isTutorSearchPage = true;
    jQuery(document).ready(function() {
        isHomePage = true;
    });
</script>
